const tabs=document.querySelectorAll(".nation__tab"),tabsContent=document.querySelectorAll(".nation__discription"),getContent=(t,e)=>{t.forEach(t=>{t.forEach(t=>{t.classList.remove("nation__discription--active")}),t[e].classList.add("nation__discription--active")})};for(let t=0;t<tabs.length;t++)tabs[t].addEventListener("click",e=>{e.preventDefault(),getContent([tabs,tabsContent],t)});